import React, { useState, useRef, useEffect } from "react";

const Timers = () => {
    const Ref = useRef(null);
    const [timer, setTimer] = useState("00:45:00");

    const getTimeRemaining = (e) => {
        const total = Date.parse(e) - Date.parse(new Date());
        const seconds = Math.floor((total / 1000) % 60);
        const minutes = Math.floor((total / 1000 / 60) % 60);
        const hours = Math.floor((total / 1000 / 60 / 60) % 24);
        return { total, hours, minutes, seconds };
    };

    const startTimer = (e) => {
        let { total, hours, minutes, seconds } = getTimeRemaining(e);
        if (total >= 0) {
            setTimer(
                (hours > 9 ? hours : "0" + hours) + ":" +
                (minutes > 9 ? minutes : "0" + minutes) + ":" +
                (seconds > 9 ? seconds : "0" + seconds)
            );
        }
    };

    const clearTimer = (e) => {
        setTimer("00:45:00");

        if (Ref.current) clearInterval(Ref.current);
        const id = setInterval(() => {
            startTimer(e);
        }, 1000);
        Ref.current = id;
    };

    const getDeadTime = () => {
        let deadline = new Date();
        deadline.setMinutes(deadline.getMinutes() + 45); // Set the deadline to 45 minutes from now
        return deadline;
    };

    useEffect(() => {
        // Timer is initially not started automatically; removed from useEffect
    }, []);

    const onClickReset = () => {
        clearTimer(getDeadTime());
    };

    const startButtonHandler = () => {
        clearTimer(getDeadTime());
    };

    return (
        <div style={{ textAlign: "center", margin: "auto" }}>
            <h2>{timer}</h2>
            <button onClick={startButtonHandler}>Start Timer</button>
            <button onClick={onClickReset}>Reset</button>
        </div>
    );
};

export default Timers;



