import {useNavigate} from "react-router-dom";
import React, { useState, useEffect } from 'react';

function Messages() {
    const [messages, setMessages] = useState([]);
    const [input, setInput] = useState('');
    const socket = new WebSocket('ws://localhost:3000');
    const navigate = useNavigate();

    useEffect(() => {
        socket.onmessage = (event) => {
            const receivedMessage = event.data;
            setMessages([...messages, receivedMessage]);
        };
    }, [messages]);


        // Select DOM elements
        const messageInput = document.getElementById('message');
        const sendButton = document.getElementById('sendButton');
        const responseDisplay = document.getElementById('response');

        // Event listener for button click
        sendButton.addEventListener('click', async () => {
          const message = messageInput.value.trim();

          if (message) {
            try {
              // Send the message to the backend using the POST method
              const response = await fetch('http://127.0.0.1:5000/api/data', {
                method: 'POST',
                headers: {
                  'Content-Type': 'application/json', // Let the backend know the content type is JSON
                },
                body: JSON.stringify({ text: message }) // Send the message as JSON in the request body
              });

              const data = await response.json();

              // Display the response from the backend
              responseDisplay.textContent = JSON.stringify(data, null, 2);
            } catch (error) {
              console.error('Error:', error);
              responseDisplay.textContent = 'Failed to send message.';
            }
          } else {
            responseDisplay.textContent = 'Please enter a message.';
          }
        });

    const sendMessage = () => {
        const response = fetch('http://127.0.0.1:5000/api/data', {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json', // Let the backend know the content type is JSON
            },
            body: JSON.stringify({ text: input }) // Send the message as JSON in the request body
          });

          const data = response.json();
          console.log(response.json());
    };


    return (
        <div className="chat-app">
            <h1>Messages</h1>
            <button onClick={() => navigate("/")}>Go to Home</button>
            <div className="messages">
                {messages.map((message, index) => (
                    <div key={index}>{message}</div>
                ))}
            </div>
            <div className="input-container">
                <input
                    type="text"
                    value={input}
                    onChange={(e) => setInput(e.target.value)}
                />
                <button onClick={sendMessage}>Send</button>
            </div>

        </div>
    );
}

export default Messages;
