import React, { useState, useEffect } from "react";

function Timers() {
    const [timers, setTimers] = useState({
        timer1: { time: 2100, display: "00:35:00" }, // Initializing with 35 minutes in seconds
        timer2: { time: 2100, display: "00:35:00" },
        timer3: { time: 2100, display: "00:35:00" },
        timer4: { time: 2700, display: "00:45:00" }, // Initializing with 45 minutes in seconds for the dryer
    });

    useEffect(() => {
        // This effect is for cleanup purposes
        return () => {
            Object.keys(timers).forEach(timerId => {
                clearInterval(timers[timerId].intervalId);
            });
        };
    }, [timers]); // Dependencies array ensures effect runs when 'timers' object changes

    const startTimer = (timerId, duration) => {
        const intervalId = setInterval(() => {
            setTimers(prevTimers => {
                const time = prevTimers[timerId].time - 1;
                const hours = Math.floor(time / 3600);
                const minutes = Math.floor((time % 3600) / 60);
                const seconds = time % 60;

                const display = `${hours < 10 ? '0' + hours : hours}:${minutes < 10 ? '0' + minutes : minutes}:${seconds < 10 ? '0' + seconds : seconds}`;

                if (time <= 0) {
                    clearInterval(prevTimers[timerId].intervalId);
                }

                return { ...prevTimers, [timerId]: { ...prevTimers[timerId], time, display } };
            });
        }, 1000);

        setTimers(prevTimers => ({ ...prevTimers, [timerId]: { ...prevTimers[timerId], intervalId } }));
    };

    const resetTimer = (timerId, initialTime) => {
        clearInterval(timers[timerId].intervalId);
        const initialSeconds = initialTime === "00:35:00" ? 2100 : 2700; // Resetting to initial values based on timer type
        setTimers(prevTimers => ({ ...prevTimers, [timerId]: { time: initialSeconds, display: initialTime } }));
    };

    return (
        <div>
            <h1>Laundry Timer</h1>
            {/* Layout for timers */}
            {Object.keys(timers).map((timerId, index) => (
                <div key={timerId}>
                    <h2>{`Timer ${index + 1}`}</h2>
                    <div className="timer">{timers[timerId].display}</div>
                    <button onClick={() => startTimer(timerId, timers[timerId].time)}>Start Timer</button>
                    <button onClick={() => resetTimer(timerId, timers[timerId].display)}>Reset Timer</button>
                </div>
            ))}
        </div>
    );
}

export default Timers;
